        let result = await fetch("http://localhost:3100/allEmp");
        result = await result.json();
        setData(result);
        setFname(result.fName)
        setLName(result.LName)
        setDob(result.Dob)
        setDoj(result.Doj)
        setDesignation(result.Designation)
        setDesignation(result.Department)

        console.log(result);
      };
