      }
  
 
    return (
    <div>
       <h1> Employee detailes</h1>
            <form>
              <div className="container m-auto">
                <div className="form mt-4 pt-4">
                  <div className="form-group">
                    FName-:
                    <input
                      type="text"
                      placeholder="Name.."
                      onChange={handleChange}
                      value={fName}
                    />
                  </div>
                  <div>
                    LName-:
                    <input
                      type="text"
                      placeholder="lName.."
                      onChange={handleChange}
                      value={lName}
                    />
                  </div>
                  <div>
                    Dob-:
                    <input
                      type="date"
                      placeholder="lName.."
                      onChange={handleChange}
                      value={Dob}
                    />
                  </div>
                  <div>
                    DOJ-:
                    <input
                      type="date"
                      placeholder="please select Joining Date..."
                      onChange={handleChange}
                      value={DOJ}
                    />
                  </div>
                  <div>
                    Salary
                    <input
                      type="number"
                      placeholder="salary.."
                      onChange={handleChange}
                      value={Salary}
                    />
                  </div>
                  <div>
                    Designation
                    <input
                      type="text"
                      placeholder="Desigination.."
                      onChange={handleChange}
                      value={Designation}
                    />
                  </div>
                  <div>
                    Department
                    <input
                      type="text"
                      placeholder="department.."
                      onChange={handleChange}
                      value={Department}
                    />
                  </div>{" "}
                  <br />
                  <span>
                    <div style={{ float: "right" }}>
                      <button
                        className="btn btn-primary btn-sm-4"
                        type="subbmit"
                        onClick={SaveAll}
                      >
                        Update 
                      </button>
                    </div>
                  </span>
                 
                </div>
              </div>
            </form>
        </div>
  );
}

export default UpdateEmp
