import React from "react";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
// import Select from 'react-select';

const User = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [fName, setFName] = useState("");
  const [lName, setLName] = useState("");
  const navigate = useNavigate();
  const savedata = async (e) => {
    e.preventDefault();
      if (!fName ) {
        alert("please fill you name ");
        return;
      }  if(!phone ){
        alert("required Number")
        return;
      }else if(phone.length <= 10 ){
        alert("number not to be  less then 10 digits");
        return;
      } else if(!lName){
      alert("please fill last name ")
  return;
      }else if(!role){
        alert("please select  role ")
        return;
      }else if (!email){
        alert("please email adress");
        return;
      }else if(!password.length>8){
        alert("your password is more than 8 digits")
        return;
      }


    const result = await fetch("http://localhost:3100/rejister", {
      method: "post",
      body: JSON.stringify({ fName,lName,role,UID, phone, email, password }),
      headers: {
        "Content-Type": "application/json",
      },
    });
    let data = await result.json();
    alert("Rejistration successfully")
    navigate("/");

  };
  return (
    <div>
      <form
        style={{
          float: "left",
          margin: "auto",
        }}
      >
        <h6>User added</h6>
        <div
          style={{
            width: 400,
            margin:"auto",
            height: "auto",
          }}
        >
          <label>First_Name</label>
          <input
            type="text"  
            className="form-control"
            placeholder="Enter Name"
            value={fName}
            onChange={(e) => setFName(e.target.value)}
          />
          <label>Last_Name</label>
          <input
            type="text"
            className="form-control"
            placeholder="Enter Name"
            // value={lName}
            onChange={(e) => setLName(e.target.value)}
          /> 
          <label>Mobile</label>
          <input

            type="number"
            maxlength="10"
            pattern="[1-9]{1}[0-9]{9}"
            className="form-control"
            placeholder="Enter phone..."
            onChange={(e) => {
               setPhone(e.target.value)
             }
          }
          />
          <div className="form-group">
            <label>Email </label>
            <input
              type="email"
              className="form-control"
              placeholder="Enter email"
              onChange={(e) => setEmail(e.target.value)}
            />
            <small className="form-text text-muted">

            </small>
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              className="form-control"
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <br />
          <button
            type="submit"
            className="btn btn-primary "
            style={{ float: "right" }}
            onClick={savedata}
          >
            Save
          </button>
        </div>
      </form>

    </div>
  );
};

export default User;
