import React from "react";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import 'react-toastify/dist/ReactToastify.css';
import Modal from "react-modal";
const customStyles = {
  content: {
    top: "40%",
    left: "70%",
    right: "auto",
    bottom: "auto",
    marginRight: "auto",
    width: "500px",
    transform: "translate(-50%, -50%)",
  },
};

const User = () => {
  const [modalIsOpen, setIsOpen] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [fName, setFName] = useState("");
  const [lName, setLName] = useState("");
  const navigate = useNavigate();
  const savedata = async (e) => {
    e.preventDefault();
      if (!fName ) {
        alert("please fill you name ");
        return;
      } 
      else if(!phone ){
        alert("required Number")
        return;
      }
      else if(phone.length <= 10 ){
        alert("number not to be  less then 10 digits");
        return;
      } 
      else if(!lName){
           alert("please fill last name ")
           return;
      }
      else if (!email){ 
        alert("please email adress");
        return;
      }
      else if(!password.length>8){
        alert("your password is more than 8 digits")
        return;
      }


    const result = await fetch("http://localhost:3100/rejister", {
      method: "post",
      body: JSON.stringify({ fName,lName, phone, email, password }),
      headers: {
        "Content-Type": "application/json",
      },
    });
    let data = await result.json();
    alert("Rejistration successfully")
    navigate("/");

  };
  // modal functions here
  const openModal = () => setIsOpen(true);
  const afterOpenModal = () => (subtitle.style.color = "");
  const closeModal = () => setIsOpen(false);
  return (
    <div>
       <div className="btn btn-primary btn-sm" style={{ background: "lightblue", float: "right" }}  >
        <button onClick={openModal}>Add Employe</button>{" "}
          </div>
          <h3 className="text-center m-auto">user detailes </h3>
          <Modal
            isOpen={modalIsOpen}
            onAfterOpen={afterOpenModal}
            onRequestClose={closeModal}
            style={customStyles}
            contentLabel="Example Modal"
          >
            <h2 ref={(_subtitle) => (subtitle = _subtitle)}>
              Employee detailes{" "}
            </h2>
            <h3>----------------------------------------</h3>

            <form>
              <div className="container m-auto">
                <div className="form mt-4 pt-4">
                  <div className="form-group">
                    FName-:
                    <input
                      type="text"
                      placeholder="Name.."
                      onChange={handleChange}
                    />
                  </div>
                  <div>
                    LName-:
                    <input
                      type="text"
                      placeholder="lName.."
                      onChange={handleChange}
                    />
                  </div>
                  <div>
                    DOJ-:
                    <input
                      type="date"
                      placeholder="please select Joining Date..."
                      onChange={handleChange}
                    />
                  </div>
                  <div>
                    Salary
                    <input
                      type="number"
                      placeholder="salary.."
                      onChange={handleChange}
                    />
                  </div>
                  <div>
                    Designation
                    <input
                      type="text"
                      placeholder="Desigination.."
                      onChange={handleChange}
                    />
                  </div>
                  <div>
                    Department
                    <input
                      type="text"
                      placeholder="department.."
                      onChange={handleChange}
                    />
                  </div>{" "}
                  <br />
                  <span>
                    <div style={{ float: "right" }}>
                      <button
                        className="btn btn-primary btn-sm-4"
                        type="subbmit"
                        onClick={SaveAll}
                      >
                        add Employee
                      </button>{" "}
                    </div>
                  </span>
                  <button
                    type="close"
                    className="btn btn-secondary btn-sm-4"
                    style={{ float: "left" }}
                    onClick={closeModal}
                  >
                    close
                  </button>
                </div>
              </div>
            </form>
          </Modal>
      <form style={{ float: "left", margin: "auto",   }} >
        <h6>User added</h6>
        <div style={{ width: 400, margin:"auto", height: "auto" }}>
         <label>First_Name</label>
          <input
            type="text"  className="form-control" placeholder="Enter Name"  value={fName} onChange={(e) => setFName(e.target.value)}  />


          <label>Last_Name</label>
          <input type="text"  className="form-control" placeholder="Enter Name" onChange={(e) => setLName(e.target.value)} /> 

          <label>Mobile</label>
          <input type="number" className="form-control" placeholder="Enter phone..." onChange={(e) => {  setPhone(e.target.value)} } />

          <div className="form-group">
          <label>Email </label>
          <input type="email" className="form-control" placeholder="Enter email" onChange={(e) => setEmail(e.target.value)} />
          <small className="form-text text-muted">

            </small>    </div>

          <div className="form-group">
            <label>Password</label>
            <input  type="password" className="form-control"  placeholder="Password" onChange={(e) => setPassword(e.target.value)}  />

          </div>
          <br />
          <button  type="submit" className="btn btn-primary "  style={{ float: "right" }}  onClick={savedata} > Save </button>

        </div>
      </form>

    </div>
  );
};

export default User;
